// Based on tutorial at https://fireship.io/lessons/react-firebase-chat-app-tutorial/
// The tutorial also includes filtering bad words and banning users for swearing, which is not included here.

import React, { useRef, useState } from 'react';
import './App.css';

import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';
import 'firebase/analytics';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

firebase.initializeApp({
    // This data is copied from adding a web app to Firebase, in the project settings
    apiKey: "AIzaSyBJhcglS9-i0liZophzKEToRMe8FiODIOk",
    authDomain: "chat-tutorial-5692d.firebaseapp.com",
    databaseURL: "https://chat-tutorial-5692d.firebaseio.com",
    projectId: "chat-tutorial-5692d",
    storageBucket: "chat-tutorial-5692d.appspot.com",
    messagingSenderId: "441905797006",
    appId: "1:441905797006:web:274684d28efcc58c8336df"

})

const auth = firebase.auth();
const firestore = firebase.firestore();
const analytics = firebase.analytics();


function App() {

  const [user] = useAuthState(auth);

  return (
    <div className="App">
      <header>
        <h1>⚛️🔥💬</h1>
        <SignOut />
      </header>

      <section>
        {user ? <GroupChatRoom /> : <SignIn />}
      </section>

    </div>
  );
}

//TODO we don't want to sign in with Google, we want to have our own signup.
function SignIn() {

  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  }

  return (
    <>
      <button className="sign-in" onClick={signInWithGoogle}>Sign in with Google</button>
      <p>Do not violate the community guidelines or you will be banned for life!</p>
    </>
  )

}

function SignOut() {
  return auth.currentUser && (
    <button className="sign-out" onClick={() => auth.signOut()}>Sign Out</button>
  )
}


function PrivateChat(){
    // Merge two user IDs and create a new chat ID
    chatID = () => {
    const chatterID = this.props.authUser.uid;
    const chateeID = this.chateeUID;
    const chatIDpre = [];
    chatIDpre.push(chatterID);
    chatIDpre.push(chateeID);
    chatIDpre.sort();
    return chatIDpre.join('_');
    };

    // Send a message to Firestore
    const sendChatMessage = (chatID, chat) => {
    return db
    .collection('messages')
    .doc(chatID)
    .collection('chats')
    .add(chat);
    };

    // Receive the messagen from Firebase
    const db = firebase.firestore();
      db.collection('messages')
        .doc(chatID)
        .collection('chats')
        .orderBy('createdAt', 'desc')
        .onSnapshot(function(doc) {
          console.log(doc.docs);         
        });
}

function GroupChatRoom() {

  const dummy = useRef();
  const messagesRef = firestore.collection('messages');
  const query = messagesRef.orderBy('createdAt').limit(25);

  const [messages] = useCollectionData(query, { idField: 'id' });

  const [formValue, setFormValue] = useState('');


  const sendMessage = async (e) => {
    e.preventDefault();

    const { uid, photoURL } = auth.currentUser;

    await messagesRef.add({
      text: formValue,
      createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      uid,
      photoURL
    })

    setFormValue('');
    dummy.current.scrollIntoView({ behavior: 'smooth' });
  }

  return (<>
    <main>

      {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}

      {/*Adding this empty dummy makes it so it scrolls to the bottom when there are new messages -->*/}
      <span ref={dummy}></span>

    </main>

    <form onSubmit={sendMessage}>

      <input value={formValue} onChange={(e) => setFormValue(e.target.value)} placeholder="say something nice" />

      <button type="submit" disabled={!formValue}>🕊️</button>

    </form>
  </>)
}


function ChatMessage(props) {
  const { text, uid, photoURL } = props.message;

  // Here we set it up so we can apply different styling based on whether the message is sent or received
  const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';

  return (<>
    <div className={`message ${messageClass}`}>
      <img src={photoURL || 'https://api.adorable.io/avatars/23/abott@adorable.png'} />
      <p>{text}</p>
    </div>
  </>)
}


export default App;

/* Note: Also edited the "Rules" in Firebase like so:
rules_version = '2';
service cloud.firestore {
  match /databases/{database}/documents {
    match /{document=**} {
      allow read, write: if
          request.time < timestamp.date(2020, 12, 19);
    }

    match /messages/{docId}{
    	allow read: if request.auth.uid != null;
      allow create: if canCreateMessage();
    }

    function canCreateMessage(){
    	let isSignedIn = request.auth.uid != null;
      let isOwner = request.auth.uid == request.resource.data.uid;

      return isSignedIn && isOwner;
    }
  }
}
*/
